mapper.select:

WHERE 1=1

	AND order_number = #{orderNumber}


	AND product_name = #{productName}


	AND internal_product_code = #{internalProductCode}


	AND quantity = #{quantity}


	AND consignee = #{consignee}


	AND contact_number = #{contactNumber}


	AND detailed_address = #{detailedAddress}


	AND date = #{date}


	AND remarks = #{remarks}


	AND express_fee = #{expressFee}


	AND logistics_cost = #{logisticsCost}


	AND brand_owner = #{brandOwner}


	AND ordering_party = #{orderingParty}


	AND purchase_price = #{purchasePrice}


	AND purchase_freight = #{purchaseFreight}


	AND purchase_other_costs = #{purchaseOtherCosts}


	AND purchase_total = #{purchaseTotal}


	AND purchase_inspection = #{purchaseInspection}


	AND purchase_status = #{purchaseStatus}


	AND purchase_remarks = #{purchaseRemarks}


	AND delivery_price = #{deliveryPrice}


	AND delivery_freight = #{deliveryFreight}


	AND delivery_other_costs = #{deliveryOtherCosts}


	AND delivery_total = #{deliveryTotal}


	AND delivery_inspection = #{deliveryInspection}


	AND delivery_status = #{deliveryStatus}


	AND delivery_remarks = #{deliveryRemarks}


	AND profit = #{profit}


	AND shipment_code = #{shipmentCode}

mapper.update:
UPDATE wholesale_record
order_number = #{orderNumber}, product_name = #{productName}, internal_product_code = #{internalProductCode}, quantity = #{quantity}, consignee = #{consignee}, contact_number = #{contactNumber}, detailed_address = #{detailedAddress}, date = #{date}, remarks = #{remarks}, express_fee = #{expressFee}, logistics_cost = #{logisticsCost}, brand_owner = #{brandOwner}, ordering_party = #{orderingParty}, purchase_price = #{purchasePrice}, purchase_freight = #{purchaseFreight}, purchase_other_costs = #{purchaseOtherCosts}, purchase_total = #{purchaseTotal}, purchase_inspection = #{purchaseInspection}, purchase_status = #{purchaseStatus}, purchase_remarks = #{purchaseRemarks}, delivery_price = #{deliveryPrice}, delivery_freight = #{deliveryFreight}, delivery_other_costs = #{deliveryOtherCosts}, delivery_total = #{deliveryTotal}, delivery_inspection = #{deliveryInspection}, delivery_status = #{deliveryStatus}, delivery_remarks = #{deliveryRemarks}, profit = #{profit}, shipment_code = #{shipmentCode}, update_time = sysdate()
WHERE wholesale_record_id = #{wholesaleRecordId}
